import React, { useEffect, useState } from "react";
import mainLogo from "../../assets/github-logo.png";
import { Select, Row, Col, Spin } from "antd";
import Card from "../../components/Card/Card";
import { Container } from "./styles";

import axios from "axios";

import { fetchRepositories } from "../../services/integrationApi";

const { Option } = Select;

const Home: React.FC = () => {
  const [selectedLanguage, setSelectedLanguage] = useState("");
  const [data, setData] = useState<any>([]);
  const [loading, setLoading] = useState<any>(false);

  const handleFetchRepositories = (language: string) => {
    setLoading(true);
    fetchRepositories(language)
      .then((res) => {
        setData(res.data.items);
        setLoading(false);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const onChange = (e: string) => {
    handleFetchRepositories(e);
  };

  useEffect(() => {
    handleFetchRepositories(selectedLanguage);

    axios.get(`/historics`).then((res) => {
      console.log("res", res);
    });
  }, []);

  useEffect(() => {
    handleFetchRepositories(selectedLanguage);
  }, [selectedLanguage]);

  return (
    <Container>
      <div>
        <img src={mainLogo} alt="fireSpot" />
      </div>
      <div className="container_select">
        <Select
          placeholder="Select a language"
          onChange={onChange}
          //   onSearch={onSearch}
          //   filterOption={(input, option) =>
          //     option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
          //   }
        >
          <Option value="java">Java</Option>
          <Option value="python">Python</Option>
          <Option value="javascript">Javascript</Option>
          <Option value="typescript">Typescript</Option>
          <Option value="ruby">Ruby</Option>
        </Select>
      </div>

      {console.log("data", data)}

      <div className="list_repo">
        {!loading && selectedLanguage ? (
          <Row gutter={[24, 24]} className="row_list">
            {data.map((item: any) => {
              return (
                <Col span={16}>
                  <Card {...item} />
                </Col>
              );
            })}
          </Row>
        ) : (
          <Spin size="large" />
        )}
      </div>
    </Container>
  );
};

export default Home;
