import React, { useEffect, useState } from "react";
import mainLogo from "../../assets/github-logo.png";
import { Select, Row, Col } from "antd";
import Card from "../../components/Card/Card";
import { Container } from "./styles";

import axios from "axios";

import { fetchRepositories } from "../../services/integrationApi";

const { Option } = Select;

const Home: React.FC = () => {
  const [selectedLanguage, setSelectedLanguage] = useState("");
  const [data, setData] = React.useState<any>([]);

  const handleFetchRepositories = (language: string) => {
    fetchRepositories(language)
      .then((res) => {
        setData(res.data.items);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const onChange = (e: string) => {
    handleFetchRepositories(e);
  };

  useEffect(() => {
    handleFetchRepositories(selectedLanguage);

    axios.get(`/historics`).then((res) => {
      console.log("res", res);
    });
  }, []);

  useEffect(() => {
    handleFetchRepositories(selectedLanguage);
  }, [selectedLanguage]);

  return (
    <Container>
      <div>
        <img src={mainLogo} alt="fireSpot" />
      </div>
      <div>
        <Select
          showSearch
          placeholder="Select a person"
          optionFilterProp="children"
          onChange={onChange}
          //   onSearch={onSearch}
          //   filterOption={(input, option) =>
          //     option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
          //   }
        >
          <Option value="java">Java</Option>
          <Option value="python">Python</Option>
          <Option value="javascript">Javascript</Option>
          <Option value="typescript">Typescript</Option>
          <Option value="ruby">Ruby</Option>
        </Select>

        {console.log("data", data)}

        <div className="list_repo">
          <Row gutter={[24, 24]}>
            {data.map((item: any) => {
              return (
                <Col span={24}>
                  <Card {...item} />
                </Col>
              );
            })}
          </Row>
        </div>
      </div>
    </Container>
  );
};

export default Home;
