import React, { useEffect, useState } from "react";
import mainLogo from "../../assets/github-logo.png";
import { Select, Row, Col, Spin, Input } from "antd";
import Card from "../../components/Card/Card";
import { Container } from "./styles";

import axios from "axios";

import { fetchRepositories } from "../../services/integrationApi";

const { Option } = Select;

const Home: React.FC = () => {
  const [selectedLanguage, setSelectedLanguage] = useState<any>(null);
  const [data, setData] = useState<any>([]);
  const [filteredData, setFilteredData] = useState<any>([]);
  const [loading, setLoading] = useState<any>(false);

  const handleFetchRepositories = (language: string) => {
    setLoading(true);
    setData([]);
    fetchRepositories(language)
      .then((res) => {
        setData(res.data.items);
        setLoading(false);
      })
      .catch((error) => {
        console.log(error);
        setLoading(false);
      });
  };

  const onChange = (e: string) => {
    setSelectedLanguage(e);
    handleFetchRepositories(e);
  };

  const handleOnChange = (e: any) => {
    const keyword = e.target.value;

    if (keyword !== "") {
      const results = data.filter((item: any) => {
        return item.title.toLowerCase().startsWith(keyword.toLowerCase());
        // Use the toLowerCase() method to make it case-insensitive
      });
      setFilteredData(results);
    } else {
      setFilteredData(data);
      // If the text field is empty, show all users
    }
  };

  useEffect(() => {
    if (data.length > 0) {
      data.map((item: any) => {
        return axios({
          method: "post",
          url: "/historic/create",
          headers: {},
          data: {
            language: item.language,
            owner: item.owner.login,
            title: item.name,
            description: item.description,
          },
        });
      });
    }
  }, [data]);

  return (
    <Container>
      <div>
        {console.log("filter", filteredData)}
        <img src={mainLogo} alt="fireSpot" data-testid="logo" />
      </div>
      <div className="container_select">
        <Select placeholder="Select a language" onChange={onChange}>
          <Option value="java">Java</Option>
          <Option value="python">Python</Option>
          <Option value="javascript">Javascript</Option>
          <Option value="typescript">Typescript</Option>
          <Option value="ruby">Ruby</Option>
        </Select>
      </div>

      <Input placeholder="Buscar..." onChange={handleOnChange}></Input>

      <div className="list_repo" data-testid="list_repo">
        {!loading ? (
          <Row gutter={[24, 24]} className="row_list">
            {filteredData.map((item: any) => {
              return (
                <Col span={14}>
                  <Card {...item} />
                </Col>
              );
            })}
          </Row>
        ) : (
          <Spin size="large" />
        )}
      </div>
    </Container>
  );
};

export default Home;
